mapper.update:
UPDATE board
	SET title = #{title}, subject = #{subject}
WHERE bo_no = #{boNo}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, a.* FROM( SELECT get_common(bo_type) bo_type, bo_no, title, subject, reg_dt, view_cnt FROM board WHERE 1=1 AND me_id = #{meId} AND title LIKE '%' || #{keyword} || '%'
AND bo_type = #{boType}
ORDER BY bo_no DESC) a ) WHERE RN between (#{page}-1)*10 and #{page}*10

		SELECT *  FROM ( SELECT ROWNUM AS rn, a.*
		                 FROM (
		                       SELECT bo.bo_no, title, reg_dt, view_cnt, me_id,
		                       		  get_common(breed) breed, get_common(city) city, get_common(sex) sex, get_common(pet_type) pet_type,
		                       		  phone, get_common(status) status
		                         FROM board bo
		                         JOIN adopt ad
		                           ON bo.bo_no = ad.bo_no
		                        WHERE 1=1
		                          AND bo_type = 'BY2'

		                          AND pet_type = #{petType}


  		                          AND breed = #{breed}


		                          AND sex = #{sex}


		                          AND city = #{city}

		                       ORDER BY bo_no DESC
		) a	) WHERE RN BETWEEN (#{page}-1)*10 and #{page}*10

		SELECT COUNT(*)
		  FROM board bo
		  JOIN adopt ad
            ON bo.bo_no = ad.bo_no
		 WHERE 1=1
		   AND bo_type = 'BY2'

           AND pet_type = #{petType}


           AND breed = #{breed}


           AND sex = #{sex}


           AND city = #{city}

mapper.update:
UPDATE reply
   SET subject = #{subject}
 WHERE comment_id = #{commentId}
