mapper.select:
SELECT TO_DATE(ordr_date, 'rrrr-MM-dd') ordr_date, ordr.ordr_id, prdt_img, prdt_nm, get_common(ship_st) ship_st, get_common(prc_st) prc_st, ordt.prdt_id, ship_prc, cnt, NVL(cnt, 0) * NVL(prc, 0) "PRICE", NVL(point, 0) + NVL(tt_prc,0) + NVL(ship_prc, 0) "TOTAL", ordt.ordt_id, prc FROM order_header ordr JOIN order_detail ordt ON ordr.ordr_id = ordt.ordr_id JOIN product pr ON ordt.prdt_id = pr.prdt_id JOIN product_img img ON pr.prdt_id = img.prdt_id WHERE 1=1 AND me_id = #{meId} AND is_main = 'Y'
AND TO_DATE(ordr_date, 'rrrr-MM-dd') BETWEEN #{start} AND #{end}
AND get_common(SHIP_ST) = #{shipSt}
AND get_common(PRC_ST) = #{prcSt}
ORDER BY 1 DESC

SELECT * FROM ( SELECT ROWNUM AS rn, a.* FROM( SELECT TO_DATE(ordr_date, 'rrrr-MM-dd') ordr_date, ordr.ordr_id, prdt_img, prdt_nm, get_common(ship_st) ship_st, get_common(prc_st) prc_st, ordt.prdt_id, ship_prc, cnt, NVL(cnt, 0) * NVL(prc, 0) "PRICE", prc, NVL(POINT, 0) + NVL(TT_PRC,0) "TOTAL", ordt.ordt_id FROM order_header ordr JOIN order_detail ordt ON ordr.ordr_id = ordt.ordr_id JOIN product pr ON ordt.prdt_id = pr.prdt_id JOIN product_img img ON pr.prdt_id = img.prdt_id WHERE 1=1 AND me_id = #{meId} AND is_main = 'Y'
AND ordr_date BETWEEN #{start} AND #{end}
AND get_common(SHIP_ST) = #{shipSt}
AND get_common(PRC_ST) = #{prcSt}
ORDER BY 2 DESC) a ) WHERE RN BETWEEN (#{page}-1)*10 and #{page}*10

		SELECT COUNT(*)
		FROM order_header ordr
		    JOIN order_detail ordt
		        ON ordr.ordr_id = ordt.ordr_id
		WHERE me_id = #{meId}

        AND TO_DATE(ordr_date, 'rrrr-MM-dd') BETWEEN #{start} AND #{end}


        AND get_common(SHIP_ST) = #{shipSt}


        AND get_common(PRC_ST) = #{prcSt}

SELECT COUNT(*)
FROM interest_product
WHERE me_id = #{meId}

mapper.delete:
DELETE FROM interest_product
WHERE me_id = #{meId}
AND prdt_id = #{prdtId}

mapper.update:
  	UPDATE order_header
	SET prc_st = 'PS3'
	WHERE ordr_id = #{ordrId}
