mapper.select:
SELECT no_no, title, rdate, view_cnt, contents
FROM notice
WHERE no_no = #{noNo}

mapper.insert:
INSERT INTO notice (
no_no
, title
, contents
, rdate
, view_cnt
, files
, me_id
)
VALUES (
(SELECT NVL(MAX(no_No), 0) + 1 FROM notice)
, #{title}
, #{contents}
, sysdate
, #{viewCnt}
, #{files}
, #{meId}
)
