mapper.select:
		SELECT no_no, title, rdate, view_cnt
		FROM
		(
			SELECT notice.*, ROWNUM RN
			FROM notice
			WHERE ROWNUM <= #{pageNo} * #{amount}  

             AND title like '%'||#{keyword}||'%' 

			ORDER BY no_no DESC
		)

		WHERE RN > (#{pageNo} - 1) * #{amount}

SELECT no_no, title, rdate, view_cnt, contents, files
FROM notice
WHERE no_no = #{noNo}

mapper.insert:
INSERT INTO notice (
no_no
, title
, contents
, rdate
, view_cnt
, files
, me_id
)
VALUES (
(SELECT NVL(MAX(no_No), 0) + 1 FROM notice)
, #{title}
, #{contents}
, sysdate
, #{viewCnt}
, #{files}
, #{meId}
)

mapper.update:
UPDATE notice
	SET title = #{title},
		contents = #{contents}
		files = #{files}
	WHERE no_no = #{noNo}

UPDATE notice
	SET view_cnt=view_cnt+1
	WHERE no_no=#{noNo}

UPDATE notice
SET files = null
WHERE no_no = #{noNo}
