mapper.update:
UPDATE 	order_header
SET		prc_st = #{prcSt}
WHERE	ordr_id = #{ordrId}

UPDATE 	order_header
SET		ship_st = #{shipSt}
WHERE	ordr_id = #{ordrId}

mapper.select:
SELECT o.ordr_id, o.me_id, o.ordr_date, o.rcv_psn, o.rcv_phone, o.rcv_addr, o.tt_prc, o.mthd, o.invo, o.ship_prc, o.prc_st, o.ship_st, o.point, o.note, m.name FROM order_header o JOIN member m ON o.me_id = m.me_id
AND m.name LIKE '%' || #{name} || '%'
AND o.mthd LIKE '%' || #{mthd} || '%'
AND o.ordr_date >= #{ordrDate} and TRUNC(o.ordr_date) <= #{endDate}
AND o.prc_st IN
#{item}
AND o.ship_st IN
#{item}
ORDER BY o.ordr_id

SELECT o.ordr_id, o.me_id, o.ordr_date, o.rcv_psn, o.rcv_phone, o.rcv_addr, o.tt_prc, o.mthd, o.invo, o.ship_prc, o.prc_st, o.ship_st, o.point, o.note, m.name, p.prdt_nm FROM order_header o JOIN member m ON o.me_id = m.me_id JOIN order_detail od ON o.ordr_id = od.ordr_id JOIN product p ON od.prdt_id = p.prdt_id
AND m.name LIKE '%' || #{name} || '%'
AND o.mthd LIKE '%' || #{mthd} || '%'
AND o.ordr_date >= #{ordrDate} and TRUNC(o.ordr_date) <= #{endDate}
AND o.prc_st IN
#{item}
AND o.ship_st IN
#{item}
ORDER BY o.ordr_id
